set(LIB_HEADERS
 # Models
 include/models/Instrument.h
 include/models/Piano.h
 include/models/Xylophone.h
 include/models/VideoGame.h
 include/models/Score.h
 include/models/Note.h

 # Views
 include/views/View.h
