void Controller::initializeButtons() {
 // Tableau des noms de boutons
 std::vector<std::string> buttonNames = {
 "Select",
 "Remove Octave",
 "Add Octave",
 "Import File",
 "Play Song",
 "Start Recording",
 "Export",
 "Finish Recording"
