 case InstrumentType::PIANO:
 headerLabel = "Piano";
 break;
 case InstrumentType::XYLOPHONE:
 headerLabel = "Xylophone";
 break;
 case InstrumentType::VIDEO_GAME:
 headerLabel = "Jeu vidéo";
 break;
 }

