+
+ // Dessiner les lignes du cercle
+ for (int i = 0; i < numPoints; i++) {
+ int next = (i + 1) % numPoints;
+ SDL_RenderLine(renderer, circle[i].x, circle[i].y, circle[next].x, circle[next].y);
+ }
