+ // Dessiner le carré à l'intérieur
+ float squareSize = size / 3;
+ SDL_FRect square = {
+ centerX - squareSize / 2,
+ centerY - squareSize / 2,
+ squareSize,
+ squareSize
+ };
+ SDL_RenderRect(renderer, &square);
