 int length = text.length() * 5; // Estimation de la longueur en pixels pour un texte plus petit
 SDL_FRect textRect = {centerX - length / 2, centerY - 5, static_cast<float>(length), 10};
 SDL_RenderFillRect(renderer, &textRect);
}
